package com.kasite.client.order.bean.dbo;import javax.persistence.Table;@Table(name="O_ORDER_ITEM")public class OrderItem { 	private String orderId;  	private String hisOrderId; 	 	private Integer unitPrice; 	 	private Integer sumOfMoney; 	 	private String specifications; 	 	private String project; 	 	private String number; 	 	private String unit; 		/**	 * @return the unit	 */	public String getUnit() {		return unit;	}	/**	 * @param unit the unit to set	 */	public void setUnit(String unit) {		this.unit = unit;	}	public String getOrderId() {		return orderId;	}	public void setOrderId(String orderId) {		this.orderId = orderId;	}	public String getHisOrderId() {		return hisOrderId;	}	public void setHisOrderId(String hisOrderId) {		this.hisOrderId = hisOrderId;	}	public Integer getUnitPrice() {		return unitPrice;	}	public void setUnitPrice(Integer unitPrice) {		this.unitPrice = unitPrice;	}	public Integer getSumOfMoney() {		return sumOfMoney;	}	public void setSumOfMoney(Integer sumOfMoney) {		this.sumOfMoney = sumOfMoney;	}	public String getSpecifications() {		return specifications;	}	public void setSpecifications(String specifications) {		this.specifications = specifications;	}	public String getProject() {		return project;	}	public void setProject(String project) {		this.project = project;	}	public String getNumber() {		return number;	}	public void setNumber(String number) {		this.number = number;	}	}